mapper.select:
SELECT

FROM gen_table_column a

WHERE a.id = #{id}

SELECT
FROM gen_table_column a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' concat('%',#{name},'%')
AND a.gen_table_id = #{genTable.id}
ORDER BY a.sort ASC

mapper.update:
UPDATE gen_table_column SET
	del_flag = '1'
WHERE gen_table_id = #{deleteByGenTableId}
