mapper.select:
SELECT , ro.office_id AS "officeList.id" FROM sys_role a LEFT JOIN sys_office o ON o.id = a.office_id LEFT JOIN sys_user_role ur ON ur.role_id = a.id LEFT JOIN sys_user u ON u.id = ur.user_id LEFT JOIN sys_role_office ro ON ro.role_id = a.id WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.useable=#{useable}
AND u.id = #{user.id}
${sqlMap.dsf} ORDER BY o.code, a.name

mapper.insert:
INSERT INTO sys_role_menu(role_id, menu_id)

	SELECT #{id}, #{menu.id}
	FROM dual

INSERT INTO sys_role_office(role_id, office_id)

	SELECT #{id}, #{office.id}
	FROM dual
