mapper.select:
SELECT

FROM sys_user a

WHERE a.id = #{id}

SELECT

FROM sys_user a

WHERE a.login_name = #{loginName} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
	a.id, a.name, a.login_name
FROM sys_user a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.office_id = #{office.id}
ORDER BY a.name

mapper.insert:
INSERT INTO sys_user_role(user_id, role_id)

	SELECT #{id}, #{role.id}
	FROM dual

mapper.update:
UPDATE sys_user SET
	password = #{password}
WHERE id = #{id}

UPDATE sys_user SET
	login_ip = #{loginIp},
	login_Date = #{loginDate}
WHERE id = #{id}
