mapper.select:
SELECT

FROM test_data a

WHERE a.id = #{id}

SELECT

FROM test_data a


	a.del_flag = #{param.DEL_FLAG_NORMAL}

		AND a.user_id = #{param.user.id}


		AND a.office_id = #{param.office.id}


		AND a.name LIKE
			'%'||#{param.name}||'%'
			concat('%',#{param.name},'%')


		AND a.sex = #{param.sex}


		AND a.in_date BETWEEN #{param.beginInDate} AND #{param.endInDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
