package ${packageName}.${moduleName}.service;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.github.flying.jeelite.common.service.BaseService;
import com.github.flying.jeelite.common.utils.StringUtils;
import ${packageName}.${moduleName}.dao.${ClassName}Dao;
import ${packageName}.${moduleName}.entity.${ClassName};
<#list table.childList as c>
import ${packageName}.${moduleName}.dao.${c.className?cap_first}Dao;
import ${packageName}.${moduleName}.entity.${c.className?cap_first};
</#list>

/**
 * ${functionName}Service
 *
 * @author ${functionAuthor}
 * @version ${functionVersion}
 */
@Service
@Transactional(readOnly = true)
public class ${ClassName}Service extends BaseService<${ClassName}Dao, ${ClassName}> {

	<#list table.childList as c>
	@Autowired
	private ${c.className?cap_first}Dao ${c.className?uncap_first}Dao;
	</#list>

	@Override
	public ${ClassName} get(String id) {
		${ClassName} ${className} = super.get(id);
		<#list table.childList as c>
		${className}.set${c.className?cap_first}List(${c.className?uncap_first}Dao.findList(new ${c.className?cap_first}(${className})));
		</#list>
		return ${className};
	}

	@Override
	@Transactional(readOnly = false)
	public int save(${ClassName} ${className}) {
		int row = super.save(${className});
	<#list table.childList as c>
		for (${c.className?cap_first} ${c.className?uncap_first} : ${className}.get${c.className?cap_first}List()) {
			if (${c.className?uncap_first}.getId() == null) {
				continue;
			}
			if (${c.className?cap_first}.DEL_FLAG_NORMAL.equals(${c.className?uncap_first}.getDelFlag())) {
				if (StringUtils.isBlank(${c.className?uncap_first}.getId())) {
					<#if c.parentExists>
						<#list c.columnList as cc>
							<#if c.parentTableFk == cc.name>
					${c.className?uncap_first}.set${cc.simpleJavaField?cap_first}(${className});
							</#if>
						</#list>
					</#if>
					row += ${c.className?uncap_first}Dao.insert(${c.className?uncap_first});
				} else {
					row += ${c.className?uncap_first}Dao.updateById(${c.className?uncap_first});
				}
			} else {
				row += ${c.className?uncap_first}Dao.delete(${c.className?uncap_first});
			}
		}
	</#list>
	   return row;
	}

	@Override
	@Transactional(readOnly = false)
	public int delete(${ClassName} ${className}) {
		int row = super.delete(${className});
	<#list table.childList as c>
		row += ${c.className?uncap_first}Dao.delete(new ${c.className?cap_first}(${className}));
	</#list>
       return row;
	}

}
